mapper.select:
SELECT 
FROM portal_cal_assign
WHERE assign_Type_=#{assignType} and assign_Id_=#{assignId}

SELECT id_,canlendar_id_,assign_type_,assign_id_,calendar_name_,assign_user_name_ FROM ( SELECT ca.id_,ca.canlendar_id_,ca.assign_type_,ca.assign_id_, cc.name_ as calendar_name_, ca.assign_user_name_ FROM portal_cal_assign ca INNER JOIN portal_calendar cc ON ca.canlendar_id_ = cc.id_ WHERE ca.assign_type_ = 1 UNION SELECT ca.id_,ca.canlendar_id_,ca.assign_type_,ca.assign_id_, cc.name_ as calendar_name_, ca.assign_user_name_ FROM portal_cal_assign ca INNER JOIN portal_calendar cc ON ca.canlendar_id_ = cc.id_ WHERE ca.assign_type_ = 2 ) temp ${ew.customSqlSegment}
ORDER BY ${orderBySql}
ORDER BY id_ DESC
